import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './components/home/index';
import Navbar from './components/layouts/Navbar';
import Landing from './components/layouts/Landing';
import Alert from './components/layouts/Alert';
import SectionLayout from './components/auth/SectionLayout';
import Dashboard from './components/dashboard/Dashboard';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import { Provider } from 'react-redux';
import store from './store';
import { loadUser } from './actions/auth';
import setAuthToken from './utils/setAuthToken';

import './App.css';

if(localStorage.token) {
  setAuthToken(localStorage.token);
}

const App = () => {

  useEffect(() => {
    store.dispatch(loadUser());
  }, []);

  return (
    <Provider store={store}>
      <Router>
          <Routes>
            <Route path="/" element={ <Home />}/>
            <Route path="/demo" >
              <Route element={ <Navbar />} />
              <Route path="/demo" element={  <Alert />} />    
              <Route path="/demo" element={ <Landing />} />       
              <Route path="/demo" element={ <SectionLayout />}>
                <Route path="/demo/register" element={ <Register /> } />
                <Route path="/demo/login" element={ <Login /> } />
                <Route path="/demo/dashboard" element={ <Dashboard />} /> 
              </Route>
            </Route>
          </Routes>
      </Router>
    </Provider>
  );
}

export default App;
