import { GET_PROFILE, CLEAR_PROFILE, PROFILE_ERROR } from "../actions/types";

const initialState = {
  profile: undefined,
  profiles: [],
  repos: [],
  loading: true,
  error: {}
}

export default function profileReducer(state=initialState, action) {
  const { type, payload } = action;

  switch(type) {
    case GET_PROFILE:
      return {
        ...state,
        profile: payload,
        loading: false,
      }
    case CLEAR_PROFILE:
      return {
        ...state,
        profile: null,
        loading: false,
        repos: [],
      }  
    case PROFILE_ERROR:
      return {
        ...state,
        error: payload,
        loading: false,
      }
    default:
      return state;
  }
}
