import axios from 'axios';
import { setAlert } from './alert';
import {
  GET_PROFILE,
  PROFILE_ERROR
} from './types';

// Get current users profile
export const getCurrentProfile = () => async dispatc => {
  try {
    const res = await axios.get('api/profile/me');

    dispatchEvent({
      type: GET_PROFILE,
      payload: res.data,
    })
  } catch (error) {
    dispatchEvent({
      tpe: PROFILE_ERROR,
      payload: { 
        msg: error.response.type, 
        status: error.response.status
      }
    })
  }

}
