import React, { Fragment } from 'react';

const styles = {
  '.card': {
    'box-shadow': '0 4px 8px 0 rgba(0, 0, 0, 0.2)',
    'max-width': '300px',
    'margin': 'auto',
    'text-align': 'center',
  },  
  '.title': {
    'color': 'grey',
    'font-size': '18px',
  },
  '.p': {
    'padding-top': '20px',
  },
  button: {
    border: 'none',
    'outline': '0',
    display: 'inline-block',
    padding: '8px',
    color: 'white',
    'background-color': '#000',
    'text-align': 'center',
    cursor: 'pointer',
    width: '100%',
    'font-size': '18px',
    ':hover' : {
      opacity: '0.7'
    }
  },  
  a: {
    'text-decoration': 'none',
    'font-size': '22px',
    'color': 'black',
    ':hover' : {
      opacity: '0.7'
    }
  }, 
}
const Home = () => {
  return (
    <Fragment>
      <div style={styles['.card']}>
          <h1>Sudhin Justin</h1>
            <p style={styles['.title']}>President & Founder, BlankSpace Consulting Inc.</p>
            <p style={styles['.p']}>Ecommerce Consultant</p>
            <p style={styles['.p']}>Demo Projects</p>
          <p><button style={styles.button}>Contact</button></p>
      </div>
    </Fragment>
  );
}

export default Home;
