import React, { Fragment, useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { Link, useParams } from 'react-router';
import Spinner from '../layouts/Spinner';
import ProfileTop from './ProfileTop';
import ProfileAbout from './ProfileAbout';
import { getProfileById } from '../../actions/profile'

const Profile = ({
  getProfileById, 
  profile: { profile: userProfile, loading },
  auth,
}) => {
  let profile;
  const params = useParams();
  const [userProfileValue, setUserProfileValue] = useState();
  console.log("profile: ", params.id)
  useEffect(() => {
    if(!userProfileValue) {
      setUserProfileValue(getProfileById(params.id));
    }
  }, [userProfileValue, getProfileById, params.id])

  return ( 
    userProfile === null || loading ? 
      <Spinner /> :
      (
        <Fragment>
          <Link to="/profiles" className="btn btn-light">Back to Profiles</Link>
          {auth.isAuthenticated && !auth.loading && params.id === userProfileValue.user._id && 
          (<Link to="/edit-profile" className="btn btn-dark">Edit Profile</Link>)}
          <div class="profile-grid my-1">
            <ProfileTop profile={userProfileValue} />
            <ProfileAbout profile={userProfileValue} />
          </div>
        </Fragment>
      )
  )
}

Profile.propTypes = {
  getProfileById: PropTypes.func.isRequired,
  profile: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
  profile: state.profile,
  auth: state.auth,
})

export default connect(mapStateToProps, { getProfileById })(Profile);
