import {
  GET_POSTS,
  POST_ERROR,
} from '../actions/types';

const initialState = {
  posts: [],
  post: null,
  loading: true,
  error: {}
}

function Post(state = initialState, action) {
  const { type, payload } = action;

  switch(type) {
    case GET_POSTS:
      return {
        ...state,
        posts: payload,
        loading: false,
      }
    case POST_ERROR:
      return {
        ...state,
        posts: payload,
        error: {

        },
        loading: false,
      }      
    default:
      return state;
  }
}

export default Post;
